import React from 'react'
import {Card} from 'react-bootstrap'


const CardSol = ({solution}, color) => {
    return (
        <Card style={{backgroundColor:{color}}}>
            {/* <img src={solution.image} style={{top: '0'}}/> */}
         <Card.Body>
           <Card.Title>NCERT</Card.Title>
           <Card.Subtitle className="mb-2 text-muted">{solution.name}</Card.Subtitle>
           <Card.Text>{solution.chapters}
           </Card.Text>
           <Card.Link href="#">Card Link</Card.Link>
           <Card.Link href="#">Get PDF</Card.Link>
         </Card.Body>
       </Card>
    )
}

export default CardSol
