import React from 'react'
import {Row,Col,Image, ListGroup, Button, Form} from 'react-bootstrap'
import classBooks from '../class-books'
import Book from '../components/Book'

const ClassBookScreen = ({match}) => {
    const classbook = classBooks.find((b) =>b._id === match.params.id)
    return (
        <div>

           <Row>
            {classbook.map((books)=>(
                <Col key={book._id} xs={6} md={4} lg={3} xl={2}>
                    <Book book={book} />
                </Col>
                  
            ))}
        </Row>
        </div>
    )
}

export default ClassBookScreen
